mapper.select:
select left(sido_code,2) sidoCode, sido_name sidoName
from sidocode
order by sidoCode;

select left(gugun_code,5) gugunCode, gugun_name gugunName
from guguncode
where left(gugun_code,2) = #{sidoCode}
order by gugunCode;

select distinct dong, code 
from houseinfo
where code = #{gugunCode}
order by dong;

select no,dong,AptName,code,jibun,lat,lng
from houseinfo
where dong=#{dong}
order by AptName;
