mapper.select:
select left(sido_code,2) sido_code, sido_name 
from sidocode
order by sido_code;

select left(gugun_code,5) gugun_code, gugun_name 
from guguncode
where left(gugun_code,2) = #{sido}
order by gugun_code;

select distinct dong, code 
from houseinfo
where code = #{gugun}
order by dong;

select no,dong,AptName,code,jibun,lat,lng
from houseinfo
where dong=#{dong}
order by AptName;
